import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [selectedMovie, setSelectedMovie] = useState(null);
    const [seats, setSeats] = useState(1);
    const [message, setMessage] = useState('');

    useEffect(() => {
        axios.get('http://localhost:5000/movies')
            .then(res => setMovies(res.data))
            .catch(err => console.error(err));
    }, []);

    const handleReserve = () => {
        if (!selectedMovie) {
            alert('Please select a movie first!');
            return;
        }

        axios.post('http://localhost:5000/reserve', {
            movieId: selectedMovie.id,
            seats
        })
            .then(res => setMessage(res.data.message))
            .catch(err => alert(err.response.data.message));
    };

    return (
        <div className="app">
            <h1>BookMyShow - Demo</h1>
            
            <div className="movie-list">
                {movies.map(movie => (
                    <div key={movie.id} className="movie-card" onClick={() => setSelectedMovie(movie)}>
                        <h2>{movie.title}</h2>
                        <p>Genre: {movie.genre}</p>
                        <p>Seats Available: {movie.seats}</p>
                        <p>Showtimes: {movie.showtimes.join(', ')}</p>
                    </div>
                ))}
            </div>

            {selectedMovie && (
                <div className="booking">
                    <h2>Booking for: {selectedMovie.title}</h2>
                    <label>Seats: 
                        <input type="number" value={seats} onChange={(e) => setSeats(+e.target.value)} />
                    </label>
                    <button onClick={handleReserve}>Reserve</button>
                </div>
            )}

            {message && <p className="message">{message}</p>}
        </div>
    );
};

export default App;
